import React, { useState, useEffect } from "react";
import Jumbotron from "../components/Jumbotron";
import Wrapper from "../components/wrapper"
import Card from "../components/card";
import API from "../utils/API";

function Home() {

    const [infoCars, setCars] = useState([]);

    useEffect(() => {
        API.getInfo().then((dbCars) => {
            console.log(dbCars.data);
            setCars(dbCars.data)
        });
    }, []);

    function Repairing(event) {
        event.preventDefault();
        let carId = event.currentTarget.getAttribute("data-value");
        //const filterTrip = trip.filter((item) => item._id === tripId);
        //console.log(tripId, filterTrip);
        console.log(carId);
        //setOneTrip(filterTrip);
    }

    return (
        <>
            <Jumbotron />
            <Wrapper>   
                <Card 
                    carsInfo = {infoCars}
                />
            </Wrapper>
        </>
    )

}

export default Home;
